import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  showWebFrameworks = false;
  showLanguages = false;
  showLibraries = false;

  toggleList(list: string): void {
    if (list === 'webFrameworks') {
      this.showWebFrameworks = !this.showWebFrameworks;
      this.showLanguages = false;
      this.showLibraries = false;
    } else if (list === 'languages') {
      this.showLanguages = !this.showLanguages;
      this.showWebFrameworks = false;
      this.showLibraries = false;
    } else if (list === 'libraries') {
      this.showLibraries = !this.showLibraries;
      this.showWebFrameworks = false;
      this.showLanguages = false;
    }
  }
}
<div class="gfg">
    <img src="assets/unc.jpeg" class="transparent-image" style="max-width: 100%; height: auto;" alt="UNC image">
    <div class="row">
        <h1 class="textname">Hi, I'm Shreeya Kantamsetty</h1>
        <p class="textp">Computer Science Student at UNC Chapel Hill</p>
        <button class="btn btn-primary" type="button"><a href="#aboutMe">Click</a></button>

    </div>
</div>

<div class="container my-container">
    <div class="row">
      <!-- Profile Picture Section -->
      <div class="col-md-12 text-center mb-4">
        <div class="circular-image">
          <img class="img-responsive center-block" src="assets/me.jpeg" alt="Profile Picture">
        </div>
      </div>
      <!-- About Me Section -->
      <div id="aboutMe" class="col-md-6">
        <h1 class="text-center mb-4">About Me</h1>
        <p class="mt-4">Hello, I'm Shreeya, a second-year undergraduate at UNC Chapel Hill pursuing a B.S. in Computer Science with double minors in Statistics and Media and Journalism. My passion lies in web and app development, particularly exploring the intersection of web development and machine learning, as well as text and data mining. As the president and founder of my school's sorority for females in computer science and engineering fields, I aim to foster a supportive community. Outside of school, I enjoy reading, watching movies, and meeting new people.</p>
      </div>
      <!-- Skills Section -->
      <div class="col-md-6">
        <h1 class="text-center mb-4">Skills</h1>
        <div class="text-center row">
            <h4>Web Frameworks</h4>
        </div>
        <div class="text-center row">
            <h4>Languages</h4>
            <p>AngularJS</p>
        </div>
        <div class="text-center row">
            <h4>Libraries</h4>
        </div>
      </div>
    </div>
  </div>
